import { useEffect, useState } from 'react';
import useProvider from "./useProvider";
export default function useNFT(address, tokenId, getNFTMetadata) {
  const [metadata, setMetadata] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState();
  const {
    getNFTMetadata: getNFTMetadataFunc
  } = useProvider({
    getNFTMetadata
  });
  useEffect(() => {
    if (!address) return;
    if (getNFTMetadataFunc) {
      setLoading(true);
      getNFTMetadataFunc({
        address,
        tokenId: tokenId ? BigInt(tokenId) : undefined
      }).then(data => {
        setMetadata(data);
      }).catch(err => {
        setError(err);
        setMetadata({});
      }).finally(() => {
        setLoading(false);
      });
    } else {
      setError(new Error('Provider is not ready'));
    }
  }, [address, tokenId, getNFTMetadataFunc]);
  return {
    loading,
    metadata,
    error
  };
}