export const browsers = {
  Chrome: 'chrome',
  Edge: 'edge',
  Firefox: 'firefox',
  Safari: 'safari'
};
export function mockBrowser(browser) {
  const oriUserAgent = window.navigator.userAgent;
  Object.defineProperty(window, 'navigator', {
    value: {
      userAgent: browsers[browser]
    }
  });
  return () => {
    Object.defineProperty(window, 'navigator', {
      value: {
        userAgent: oriUserAgent
      }
    });
  };
}